import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
// import { Enfamils } from "./enfamils";

const Products = () => {
  const [locator, setLocator] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const getLocator = () => {
    axios.get("http://localhost:8000/api/products").then((response) => {
      setLocator(response.data);
    });
  };
  useEffect(() => {
    getLocator();
  }, []);

  return (
    <>
      <div className="flex content-center flex-wrap  h-10">
        <input
          type="text"
          placeholder="Search..."
          // value={locator}
          className="p-10px text-lg box-border content-center h-10 w-50 p-4 border-2 border-gray-400 bg-gray-200"
          onChange={(event) => {
            setSearchTerm(event.target.value);
          }}
        />
      </div>
      {/* <ul className="p-0 list-none text-blue-600  "> */}
      {locator
        .filter((locator) => {
          if (searchTerm == "") {
            return locator;
          } else if (
            locator.name.toLowerCase().includes(searchTerm.toLowerCase())
          ) {
            return locator;
          }
        })
        .map((locator) => (
          <div className=" flex justify-center items-center w-60 p-2 bg-white rounded-x1 transform transition all hover: translate-y-2">
            <div
              key={locator.id}
              className="bg-blue-200 rounded overflow-hidden shadow-md text-black-500 font-bold "
            >
              {locator.name}
              <img
                className="h-40 object-cover rounded x1"
                src={locator.image}
              />
              <span> Price:${locator.price}</span>
              <div className="m-2">
                <a
                  role="button"
                  href="#"
                  className="text-white
                  bg-blue-600
                  px-3
                  py-1
                  rounded-md"
                >
                  Learn more
                </a>
              </div>
            </div>
          </div>
        ))}
      {/* </ul> */}
    </>
  );
};

export default Products;
