import React from "react";
import {
  Marker,
  Autocomplete,
  DirectionsRenderer,
} from "@react-google-maps/api";
import { useRef, useState } from "react";
import { FaLocationArrow, FaTimes } from "react-icons/fa";
import { GoogleMap, LoadScript } from "@react-google-maps/api";

const center = { lat: 39.768402, lng: -86.158066 };

const Locator = () => {
  // const [libraries] = useState(["places"]);

  // const { isLoaded, loadError } = useJsApiLoader({
  //   googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  //   libraries,
  // });
  // const { isLoaded } = useJsApiLoader({
  //   googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  //   libraries: ["places"],
  // });

  const [map, setMap] = useState(/** @type google.maps.Map */ (null));
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [distance, setDistance] = useState("");
  const [duration, setDuration] = useState("");
  /** @type React.MutableRefObject<HTMLInputElement> */
  const originRef = useRef();
  /** @type React.MutableRefObject<HTMLInputElement> */
  const destiantionRef = useRef();

  if (!isLoaded) {
    return <h1>Loading ...</h1>;
  }
  async function calculateRoute() {
    if (originRef.current.value === "" || destiantionRef.current.value === "") {
      return;
    }
    // eslint-disable-next-line no-undef
    const directionsService = new google.maps.DirectionsService();
    const results = await directionsService.route({
      origin: originRef.current.value,
      destination: destiantionRef.current.value,
      // eslint-disable-next-line no-undef
      travelMode: google.maps.TravelMode.DRIVING,
    });
    setDirectionsResponse(results);
    setDistance(results.routes[0].legs[0].distance.text);
    setDuration(results.routes[0].legs[0].duration.text);
  }

  function clearRoute() {
    setDirectionsResponse(null);
    setDistance("");
    setDuration("");
    originRef.current.value = "";
    destiantionRef.current.value = "";
  }

  return (
    <LoadScript
      googleMapsApiKey=" AIzaSyDAuyBiMJVDZGrMjL1vUkV9uZgzpSLUnIU
    "
    >
      >
      <div className=" h-screen  w-screen flex relative flex-col content-center ">
        <div className=" absolute ml:0 mt:0 h-full w-full ">
          {/* Google Map Box */}
          <GoogleMap
            center={center}
            zoom={15}
            mapContainerStyle={{ width: "75%", height: "75%" }}
            options={{
              zoomControl: false,
              streetViewControl: false,
              mapTypeControl: false,
              fullscreenControl: false,
            }}
            onLoad={(map) => setMap(map)}
          >
            <Marker position={center} />
            {directionsResponse && (
              <DirectionsRenderer directions={directionsResponse} />
            )}
          </GoogleMap>
        </div>
        <div className=".rounded-md shadow-md min-w-0 bg-blue-100 z-10">
          <div className="tracking-wider space-y-0">
            <div className="flex-grow-0">
              <Autocomplete>
                <input type="text" placeholder="Origin" ref={originRef} />
              </Autocomplete>
            </div>
            <div className="flex-grow-1">
              <Autocomplete>
                <input
                  type="text"
                  placeholder="Destination"
                  ref={destiantionRef}
                />
              </Autocomplete>
            </div>

            <div>
              <button
                className="content-center py-6 px-5 mt-5 bg-pink-500 rounded-sm text-3xt
            hover:bg-gray-300 transition duration-300 ease-in-out flex items-center"
                type="submit"
                onClick={calculateRoute}
              >
                Calculate Route
              </button>
              <div
                aria-label="center back"
                icon={<FaTimes />}
                onClick={clearRoute}
              />
            </div>
          </div>
          <div spacing={4} mt={4} justifyContent="space-between">
            <h6>Distance: {distance} </h6>
            <h6>Duration: {duration} </h6>
            <div
              aria-label="center back"
              icon={<FaLocationArrow />}
              isRound
              onClick={() => {
                map.panTo(center);
                map.setZoom(15);
              }}
            />
          </div>
        </div>
      </div>
    </LoadScript>
  );
};

export default Locator;
